import { useEffect, useState } from "react";
import { Card, CardContent } from "@/components/ui/card";

export default function Centros() {
  const [centros, setCentros] = useState([]);

  useEffect(() => {
    fetch("http://gestion.local/api/centros")
      .then((response) => response.json())
      .then((data) => setCentros(data))
      .catch((error) => console.error("Error al cargar los centros:", error));
  }, []);

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 p-4">
      {centros.map((centro) => (
        <Card key={centro.id} className="p-4 shadow-lg rounded-lg">
          <img
            src={centro.foto}
            alt={centro.nombre}
            className="w-full h-40 object-cover rounded-lg"
          />
          <CardContent>
            <h2 className="text-xl font-bold">{centro.nombre}</h2>
            <p className="text-gray-600">{centro.direccion}</p>
            <p className="text-gray-600">Tel: {centro.telefono}</p>
            <p className="text-gray-600">Horario: {centro.horario}</p>
          </CardContent>
        </Card>
      ))}
    </div>
  );
}
