// src/App.js
import { BrowserRouter as Router, Route, Routes, useLocation } from "react-router-dom";
import './App.css';
import Centros from './Centros';
import CentroUnico from './CentroUnico';

function App() {
  const location = useLocation(); // Hook para obtener la ubicación actual

  // Determina si estamos en la página principal o en un centro específico
  const isCentroUnico = location.pathname.includes("/centro/");

  return (
    <Router>
      <div className="App">
        <h1 className="text-center text-2xl font-bold my-4">
          {isCentroUnico ? "Centro Cívico" : "Centros Cívicos"}
        </h1>
        <Routes>
          <Route path="/" element={<Centros />} />
          <Route path="/centro/:id" element={<CentroUnico />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
