import { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link, useParams } from "react-router-dom";

function Centros() {
  const [centros, setCentros] = useState([]);

  useEffect(() => {
    fetch("http://gestion.local/api/centros")
      .then((response) => response.json())
      .then((data) => setCentros(data))
      .catch((error) => console.error("Error al cargar los centros:", error));
  }, []);

  return (
    <div style={{ display: "grid", gap: "20px", padding: "20px" }}>
      {centros.map((centro) => (
        <Link key={centro.id} to={`/centro/${centro.id}`} style={{ textDecoration: "none", color: "inherit" }}>
          <div
            style={{
              border: "1px solid #ddd",
              borderRadius: "10px",
              padding: "15px",
              boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
            }}
          >
            <img src={centro.foto} alt={centro.nombre} style={{ width: "100%", borderRadius: "10px" }} />
            <h2>{centro.nombre}</h2>
            <p>{centro.direccion}</p>
            <p>Tel: {centro.telefono}</p>
            <p>Horario: {centro.horario}</p>
          </div>
        </Link>
      ))}
    </div>
  );
}

function CentroDetalle() {
  const { id } = useParams();
  const [centro, setCentro] = useState(null);

  useEffect(() => {
    fetch(`http://gestion.local/api/centros/${id}`)
      .then((response) => response.json())
      .then((data) => setCentro(data))
      .catch((error) => console.error("Error al cargar el centro:", error));
  }, [id]);

  if (!centro) return <p>Cargando...</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h1>{centro.nombre}</h1>
      <img src={centro.foto} alt={centro.nombre} style={{ width: "100%", borderRadius: "10px" }} />
      <p>Dirección: {centro.direccion}</p>
      <p>Teléfono: {centro.telefono}</p>
      <p>Horario: {centro.horario}</p>
      <h2>Instalaciones</h2>
      <ul>
        {centro.instalaciones && centro.instalaciones.map((instalacion) => (
          <li key={instalacion.id}>
            <strong>{instalacion.nombre}</strong>: {instalacion.descripcion} (Capacidad: {instalacion.capacidad_maxima})
          </li>
        ))}
      </ul>
    </div>
  );
}

export default function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Centros />} />
        <Route path="/centro/:id" element={<CentroDetalle />} />
      </Routes>
    </Router>
  );
}
