// src/Instalaciones.js
import { useState, useEffect } from "react";

function Instalaciones() {
  const [instalaciones, setInstalaciones] = useState([]);
  const [nombreFiltro, setNombreFiltro] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Función para obtener instalaciones
  const obtenerInstalaciones = async (nombre = "") => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch("http://gestion.local/api/instalaciones", {
        method: "GET",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ nombre }), // Enviar el nombre en el body
      });

      if (!response.ok) {
        throw new Error("Error al obtener las instalaciones");
      }

      const data = await response.json();
      setInstalaciones(data);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  // Cargar instalaciones al montar el componente
  useEffect(() => {
    obtenerInstalaciones();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Listado de Instalaciones</h2>

      {/* Campo de búsqueda */}
      <div style={{ marginBottom: "15px" }}>
        <input
          type="text"
          placeholder="Buscar por nombre..."
          value={nombreFiltro}
          onChange={(e) => setNombreFiltro(e.target.value)}
          style={{ padding: "8px", marginRight: "10px", borderRadius: "5px" }}
        />
        <button onClick={() => obtenerInstalaciones(nombreFiltro)} style={{ padding: "8px 12px", cursor: "pointer" }}>
          Buscar
        </button>
      </div>

      {/* Estado de carga y error */}
      {loading && <p>Cargando...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* Lista de instalaciones */}
      <div style={{ display: "grid", gap: "20px" }}>
        {instalaciones.length > 0 ? (
          instalaciones.map((instalacion) => (
            <div
              key={instalacion.id}
              style={{
                border: "1px solid #ddd",
                borderRadius: "10px",
                padding: "15px",
                boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
              }}
            >
              <h3>{instalacion.nombre}</h3>
              <p><strong>Descripción:</strong> {instalacion.descripcion}</p>
              <p><strong>Capacidad máxima:</strong> {instalacion.capacidad_maxima}</p>
            </div>
          ))
        ) : (
          <p>No hay instalaciones disponibles.</p>
        )}
      </div>
    </div>
  );
}

export default Instalaciones;
