// src/Login.js
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Login({ setIsAuthenticated }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const jwt = localStorage.getItem("jwt");
    if (jwt) {
      // Si ya está autenticado, redirige a la página principal
      navigate("/");
    }
  }, [navigate]);

  const handleLogin = (e) => {
    e.preventDefault();

    // Preparamos los datos que se enviarán al servidor
    const userData = {
      email,
      password,
    };

    fetch("http://gestion.local/api/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(userData),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.jwt) {
          // Si recibimos el token JWT, lo guardamos en localStorage
          localStorage.setItem("jwt", data.jwt);
          localStorage.setItem("expira", data.expira);

          // Redirigimos a la página principal u otra página de tu elección
          setIsAuthenticated(true); // Actualizamos el estado
          navigate("/"); // Redirige al home
        } else {
          // Si no se recibe el JWT, mostramos un mensaje de error
          setError(data.message || "Error desconocido");
        }
      })
      .catch((error) => {
        setError("Error al iniciar sesión. Intenta de nuevo.");
        console.error("Error en la petición de login:", error);
      });
  };

  return (
    <div style={{ padding: "20px", maxWidth: "400px", margin: "auto" }}>
      <h1>Iniciar sesión</h1>

      <form onSubmit={handleLogin}>
        <div>
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Contraseña</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <button type="submit">Iniciar sesión</button>
        </div>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}

export default Login;
