import { useEffect, useState } from "react";

export default function Centros() {
  const [centros, setCentros] = useState([]);
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    fetch("http://gestion.local/api/centros")
      .then((response) => response.json())
      .then((data) => setCentros(data))
      .catch((error) => console.error("Error al cargar los centros:", error));
  }, []);

  return (
    <div>
      {/* Menú Hamburguesa */}
      <nav style={{ padding: "10px", background: "#333", color: "white" }}>
        <button 
          onClick={() => setMenuOpen(!menuOpen)}
          style={{ background: "none", border: "none", color: "white", fontSize: "20px" }}
        >
          ☰
        </button>
        {menuOpen && (
          <ul style={{ listStyle: "none", padding: "10px", background: "#444", position: "absolute" }}>
            <li><a href="#" style={{ color: "white", textDecoration: "none" }}>Centros</a></li>
            <li><a href="#" style={{ color: "white", textDecoration: "none" }}>Actividades</a></li>
            <li><a href="#" style={{ color: "white", textDecoration: "none" }}>Instalaciones</a></li>
            <li><a href="#" style={{ color: "white", textDecoration: "none" }}>Más</a></li>
            <li><a href="#" style={{ color: "white", textDecoration: "none" }}>Login</a></li>
            <li><a href="#" style={{ color: "white", textDecoration: "none" }}>Register</a></li>
          </ul>
        )}
      </nav>

      {/* Contenido de Centros */}
      <div style={{ display: "grid", gap: "20px", padding: "20px" }}>
        {centros.map((centro) => (
          <div
            key={centro.id}
            style={{
              border: "1px solid #ddd",
              borderRadius: "10px",
              padding: "15px",
              boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
            }}
          >
            <img
              src={centro.foto}
              alt={centro.nombre}
              style={{ width: "100%", borderRadius: "10px" }}
            />
            <h2>{centro.nombre}</h2>
            <p>{centro.direccion}</p>
            <p>Tel: {centro.telefono}</p>
            <p>Horario: {centro.horario}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
