import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function CentroUnico({ setTitulo }) {
  const { id } = useParams();
  const [centro, setCentro] = useState(null);

  useEffect(() => {
    fetch(`http://gestion.local/api/centros/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setCentro(data);
        setTitulo(data.nombre); // Actualiza el título al nombre del centro
      })
      .catch((error) => console.error("Error al cargar el centro:", error));
  }, [id, setTitulo]);

  if (!centro) {
    return <p>Cargando...</p>;
  }

  // Verificamos si centro.instalaciones está definido y es un array antes de intentar mapearlo
  const instalaciones = Array.isArray(centro.instalaciones) ? centro.instalaciones : [];

  return (
    <div style={{ padding: "20px" }}>
      <h1>{centro.nombre}</h1>
      <img src={centro.foto} alt={centro.nombre} style={{ width: "100%", borderRadius: "10px" }} />
      <p><strong>Dirección:</strong> {centro.direccion}</p>
      <p><strong>Teléfono:</strong> {centro.telefono}</p>
      <p><strong>Horario:</strong> {centro.horario}</p>

      <h3>Instalaciones</h3>
      <div style={{ display: "grid", gap: "20px" }}>
        {instalaciones.length > 0 ? (
          instalaciones.map((instalacion) => (
            <div
              key={instalacion.id}
              style={{
                border: "1px solid #ddd",
                borderRadius: "10px",
                padding: "15px",
                boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
              }}
            >
              <h4>{instalacion.nombre}</h4>
              <p><strong>Descripción:</strong> {instalacion.descripcion}</p>
              <p><strong>Capacidad máxima:</strong> {instalacion.capacidad_maxima}</p>
            </div>
          ))
        ) : (
          <p>No hay instalaciones disponibles.</p>
        )}
      </div>
    </div>
  );
}

export default CentroUnico;
