// src/App.js
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import './App.css';
import Centros from './Centros';
import CentroUnico from './CentroUnico';
// import Instalaciones from './Instalaciones';
// import Actividades from './Actividades';
import Navbar from './NavBar';
import { useState } from "react";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Estado para saber si el usuario está autenticado
  const [titulo, setTitulo] = useState("Centros Cívicos"); // Título global

  const login = () => {
    setIsAuthenticated(true);
  };

  const logout = () => {
    setIsAuthenticated(false);
  };

  return (
    <Router>
      <div className="App">
        <Navbar isAuthenticated={isAuthenticated} logout={logout} />
        <div style={{ padding: "20px" }}>
          <h1>{titulo}</h1> {/* Aquí se muestra el título */}
          <Routes>
            <Route path="/" element={<Centros />} />
            <Route path="/centro/:id" element={<CentroUnico setTitulo={setTitulo} />} />
            <Route path="/instalaciones" element={<Instalaciones />} />
            <Route path="/actividades" element={<Actividades />} />
            <Route path="/login" element={<div>Login</div>} />
            <Route path="/registro" element={<div>Registro</div>} />
            <Route path="/mi-cuenta" element={<div>Mi Cuenta</div>} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
