import { useEffect, useState } from "react";

export default function Centros() {
  const [centros, setCentros] = useState([]);
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    fetch("http://gestion.local/api/centros")
      .then((response) => response.json())
      .then((data) => setCentros(data))
      .catch((error) => console.error("Error al cargar los centros:", error));
  }, []);

  return (
    <div>
      {/* Menú Hamburguesa */}
      <nav style={{ padding: "10px", background: "#333", color: "white", position: "relative" }}>
        <button 
          onClick={() => setMenuOpen(!menuOpen)}
          style={{ background: "none", border: "none", color: "white", fontSize: "20px" }}
        >
          ☰
        </button>
        {menuOpen && (
          <ul style={{
            listStyle: "none",
            padding: "10px",
            background: "#444",
            position: "absolute",
            top: "40px",
            left: "0",
            width: "100%",
            borderRadius: "5px",
            boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)"
          }}>
            <li><button style={{ background: "none", border: "none", color: "white", textDecoration: "none", display: "block", padding: "5px", width: "100%", textAlign: "center" }}>Centros</button></li>
            <li><button style={{ background: "none", border: "none", color: "white", textDecoration: "none", display: "block", padding: "5px", width: "100%", textAlign: "left" }}>Actividades</button></li>
            <li><button style={{ background: "none", border: "none", color: "white", textDecoration: "none", display: "block", padding: "5px", width: "100%", textAlign: "left" }}>Instalaciones</button></li>
            <li><button style={{ background: "none", border: "none", color: "white", textDecoration: "none", display: "block", padding: "5px", width: "100%", textAlign: "left" }}>Login</button></li>
            <li><button style={{ background: "none", border: "none", color: "white", textDecoration: "none", display: "block", padding: "5px", width: "100%", textAlign: "left" }}>Register</button></li>
          </ul>
        )}
      </nav>

      {/* Contenido de Centros */}
      <div style={{ display: "grid", gap: "20px", padding: "20px" }}>
        {centros.map((centro) => (
          <div
            key={centro.id}
            style={{
              border: "1px solid #ddd",
              borderRadius: "10px",
              padding: "15px",
              boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
            }}
          >
            <img
              src={centro.foto}
              alt={centro.nombre}
              style={{ width: "100%", borderRadius: "10px" }}
            />
            <h2>{centro.nombre}</h2>
            <p>{centro.direccion}</p>
            <p>Tel: {centro.telefono}</p>
            <p>Horario: {centro.horario}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
