// src/App.js
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { useState, useEffect } from "react";
import './App.css';
import Centros from './Centros';
import CentroUnico from './CentroUnico';
import Login from './Login';
import Registro from "./Registro";
import Navbar from './Navbar';
import Logout from './Logout';
import MiCuenta from "./MiCuenta";
import Instalaciones from "./Instalaciones";

function App() {
  const [titulo, setTitulo] = useState("Centros Cívicos");
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const jwt = localStorage.getItem("jwt");
    if (jwt) {
      setIsAuthenticated(true);
    }
  }, []);

  useEffect(() => {
    const handleStorageChange = () => {
      const jwt = localStorage.getItem("jwt");
      setIsAuthenticated(!!jwt); // Actualiza el estado en función de si el JWT está presente
    };

    // Escuchar cambios en el localStorage
    window.addEventListener("storage", handleStorageChange);

    // Limpiar el listener cuando el componente se desmonte
    return () => {
      window.removeEventListener("storage", handleStorageChange);
    };
  }, []);

  return (
    <Router>
      <div className="App">
        <Navbar isAuthenticated={isAuthenticated} />
        <header>
          <h1>{titulo}</h1>
        </header>

        <Routes>
          <Route path="/" element={<Centros />} />
          <Route path="/centro/:id" element={<CentroUnico setTitulo={setTitulo} />} />
          <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
          <Route path="/registro" element={<Registro />} />
          <Route path="/logout" element={<Logout setIsAuthenticated={setIsAuthenticated} />} /> {/* Pasamos setIsAuthenticated */}
          // src/App.js
<Route path="/mi-cuenta" element={<MiCuenta setIsAuthenticated={setIsAuthenticated} />} />
          <Route path="/instalaciones" element={<Instalaciones />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
