import { useState, useEffect } from "react";

function Instalaciones() {
  const [instalaciones, setInstalaciones] = useState([]);
  const [nombreFiltro, setNombreFiltro] = useState("");
  const [loading] = useState(false);
  const [error, setError] = useState(null);

  const obtenerInstalaciones = async (nombre) => {
    try {
      const url = 'http://gestion.local/api/instalaciones';
  
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ nombre })
      });
  
      const textResponse = await response.text();  // Leemos la respuesta como texto
  
      // Si la respuesta es válida en formato JSON, la parseamos
      try {
        const instalaciones = JSON.parse(textResponse);
        setInstalaciones(instalaciones);
      } catch (jsonError) {
        console.error("Respuesta no es JSON:", textResponse);
      }
    } catch (error) {
      console.error('Error:', error);
      setError(error.message);
    }
  };
  
  

  // Usamos un useEffect con un 'debounce' para retrasar la búsqueda
  useEffect(() => {
    const timeoutId = setTimeout(() => {
      obtenerInstalaciones(nombreFiltro);
    }, 500); // Retraso de 500ms después de dejar de escribir

    // Limpiar el timeout si el valor de nombreFiltro cambia antes de los 500ms
    return () => clearTimeout(timeoutId);
  }, [nombreFiltro]); // Solo se ejecuta cuando el nombreFiltro cambia

  return (
    <div style={{ padding: "20px" }}>
      <h2>Listado de Instalaciones</h2>

      {/* Campo de búsqueda */}
      <div style={{ marginBottom: "15px" }}>
        <input
          type="text"
          placeholder="Buscar por nombre..."
          value={nombreFiltro}
          onChange={(e) => setNombreFiltro(e.target.value)}
          style={{ padding: "8px", marginRight: "10px", borderRadius: "5px" }}
        />
      </div>

      {/* Estado de carga y error */}
      {loading && <p>Cargando...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* Lista de instalaciones */}
      <div style={{ display: "grid", gap: "20px" }}>
        {instalaciones.length > 0 ? (
          instalaciones.map((instalacion) => (
            <div
              key={instalacion.id}
              style={{
                border: "1px solid #ddd",
                borderRadius: "10px",
                padding: "15px",
                boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
              }}
            >
              <h3>{instalacion.nombre}</h3>
              <p><strong>Descripción:</strong> {instalacion.descripcion}</p>
              <p><strong>Capacidad máxima:</strong> {instalacion.capacidad_maxima}</p>
            </div>
          ))
        ) : (
          <p>No hay instalaciones disponibles.</p>
        )}
      </div>
    </div>
  );
}

export default Instalaciones;
