// src/Registro.js
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function Registro() {
    const [nombre, setNombre] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const jwt = localStorage.getItem("jwt");
        if (jwt) {
            navigate("/"); // Redirige al home si ya está autenticado
        }
    }, [navigate]);

    const handleRegister = (e) => {
        e.preventDefault();

        const userData = { nombre, email, password };

        fetch("http://gestion.local/api/register", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(userData),
        })
            .then((response) => {
                // Verificamos si la respuesta es 201 Created
                if (response.ok && response.status === 201) {
                    return response.text(); // Usamos text() porque la respuesta es texto plano
                } else {
                    // Si no es 201, mostramos el código de respuesta y el mensaje
                    return response.text().then((text) => {
                        throw new Error(`Error en la respuesta: ${text || 'Error desconocido'} (Status: ${response.status})`);
                    });
                }
            })
            .then((data) => {
                if (data === "\"Usuario creado\"") {
                    navigate("/login"); // Redirige al login
                } else {
                    setError("Error desconocido");
                }
            })
            .catch((error) => {
                console.error("Error en la petición de registro:", error);
                setError(error.message || "Error al registrarse. Intenta de nuevo.");
            });
    };

    return (
        <div style={{ padding: "20px", maxWidth: "400px", margin: "auto" }}>
            <h1>Registro</h1>

            <form onSubmit={handleRegister}>
                <div>
                    <label>Nombre</label>
                    <input
                        type="text"
                        value={nombre}
                        onChange={(e) => setNombre(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Email</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Contraseña</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <button type="submit">Registrar</button>
                </div>
            </form>

            {error && <p style={{ color: "red" }}>{error}</p>}
        </div>
    );
}

export default Registro;
