// src/App.js
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { useState, useEffect } from "react";
import './App.css';
import Centros from './Centros';
import CentroUnico from './CentroUnico';
import Login from './Login';
import Registro from "./Registro";
import Navbar from './NavBar';
import Logout from './Logout';
import MiCuenta from "./MiCuenta";

function App() {
  const [titulo, setTitulo] = useState("Centros Cívicos");
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const jwt = localStorage.getItem("jwt");
    if (jwt) {
      setIsAuthenticated(true);
    }
  }, []);

  return (
    <Router>
      <div className="App">
        <Navbar isAuthenticated={isAuthenticated} />
        <header>
          <h1>{titulo}</h1>
        </header>

        <Routes>
          <Route path="/" element={<Centros />} />
          <Route path="/centro/:id" element={<CentroUnico setTitulo={setTitulo} />} />
          <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
          <Route path="/registro" element={<Registro />} />
          <Route path="/logout" element={<Logout setIsAuthenticated={setIsAuthenticated} />} /> {/* Pasamos setIsAuthenticated */}
          <Route path ="/mi-cuenta" element={<MiCuenta />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
