// src/Actividades.js
import React, { useState, useEffect } from 'react';

const Actividades = () => {
  const [actividades, setActividades] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredActividades, setFilteredActividades] = useState([]);

  useEffect(() => {
    // Función para obtener las actividades de la API
    const fetchActividades = async () => {
      try {
        const response = await fetch('http://gestion.local/api/actividades');
        const data = await response.json();
        setActividades(data);
        setFilteredActividades(data); // Al principio mostramos todas las actividades
      } catch (error) {
        console.error('Error al cargar las actividades:', error);
      }
    };

    fetchActividades();
  }, []);

  // Función que filtra las actividades en función del término de búsqueda
  const handleSearch = (event) => {
    const search = event.target.value;
    setSearchTerm(search);

    // Filtra las actividades basándose en el nombre
    const filtered = actividades.filter(actividad =>
      actividad.nombre.toLowerCase().includes(search.toLowerCase())
    );
    setFilteredActividades(filtered);
  };

  return (
    <div className="actividades">
      <h1>Actividades</h1>

      <input
        type="text"
        placeholder="Buscar por nombre..."
        value={searchTerm}
        onChange={handleSearch}
      />

      <div>
        {filteredActividades.length > 0 ? (
          <ul>
            {filteredActividades.map(actividad => (
              <li key={actividad.id}>
                <h3>{actividad.nombre}</h3>
                <p>{actividad.descripcion}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No se encontraron actividades.</p>
        )}
      </div>
    </div>
  );
};

export default Actividades;
