import React, { useState, useEffect } from "react";
import "./Reservas.css"; // Asegúrate de que el CSS esté actualizado

const Reservas = () => {
  const [reservas, setReservas] = useState([]);
  const [instalaciones, setInstalaciones] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    // Obtener las reservas
    const fetchReservas = async () => {
      try {
        const token = localStorage.getItem("jwt");
        if (!token) {
          throw new Error("No se ha encontrado el token de autenticación.");
        }

        const response = await fetch("http://gestion.local/api/reservas", {
          method: "GET",
          headers: {
            "Authorization": `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        if (!response.ok) {
          throw new Error(`Error: ${response.status} ${response.statusText}`);
        }

        const data = await response.json();
        setReservas(data.reservas);
      } catch (error) {
        setError("Error al cargar las reservas");
        console.error("Error al obtener las reservas:", error);
      }
    };

    // Obtener las instalaciones
    const fetchInstalaciones = async () => {
      try {
        const response = await fetch("http://gestion.local/api/instalaciones");
        const data = await response.json();
        setInstalaciones(data);
      } catch (error) {
        setError("Error al cargar las instalaciones");
        console.error("Error al obtener las instalaciones:", error);
      }
    };

    fetchReservas();
    fetchInstalaciones();
  }, []);

  // Función para obtener el nombre de la instalación basado en el id
  const getInstalacionNombre = (id) => {
    const instalacion = instalaciones.find((instalacion) => instalacion.id === id);
    return instalacion ? instalacion.nombre : "Instalación no encontrada";
  };

  return (
    <div className="reservas-container">
      <h1 className="reservas-title">Reservas</h1>

      {error && <p className="error-message">{error}</p>}

      <div className="reservas-list">
        {reservas.length > 0 ? (
          reservas.map((reserva) => (
            <div key={reserva.id} className="reserva-card">
              <div className="reserva-info">
                <p><strong>Solicitante:</strong> {reserva.nombre_solicitante}</p>
                <p><strong>Teléfono:</strong> {reserva.telefono}</p>
                <p><strong>Correo:</strong> {reserva.correo}</p>
                <p><strong>Instalación:</strong> {getInstalacionNombre(reserva.instalacion_id)}</p>
                <p><strong>Fecha y hora inicio:</strong> {reserva.fecha_hora_inicio}</p>
                <p><strong>Fecha y hora final:</strong> {reserva.fecha_hora_final}</p>
                <p><strong>Estado:</strong> {reserva.estado}</p>
              </div>
            </div>
          ))
        ) : (
          <p className="no-results">No se encontraron reservas.</p>
        )}
      </div>
    </div>
  );
};

export default Reservas;
