// src/Centros.js
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function Centros({ setTitulo }) {
  const [centros, setCentros] = useState([]);

  useEffect(() => {
    fetch("http://gestion.local/api/centros")
      .then((response) => response.json())
      .then((data) => setCentros(data))
      .catch((error) => console.error("Error al cargar los centros:", error));
  }, []);

  useEffect(() => {
    setTitulo("Centros Cívicos"); // Establecer el título cuando se está en la página de todos los centros
  }, [setTitulo]);

  return (
    <div style={{ display: "grid", gap: "20px", padding: "20px" }}>
      {centros.map((centro) => (
        <Link key={centro.id} to={`/centro/${centro.id}`} style={{ textDecoration: "none", color: "inherit" }}>
          <div
            style={{
              border: "1px solid #ddd",
              borderRadius: "10px",
              padding: "15px",
              boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
            }}
          >
            <img src={centro.foto} alt={centro.nombre} style={{ width: "100%", borderRadius: "10px" }} />
            <h2>{centro.nombre}</h2>
            <p>{centro.direccion}</p>
            <p>Tel: {centro.telefono}</p>
            <p>Horario: {centro.horario}</p>
          </div>
        </Link>
      ))}
    </div>
  );
}

export default Centros;
