import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function CentroUnico() {
  const { id } = useParams(); // Obtiene el ID del centro desde la URL
  const [centro, setCentro] = useState(null);

  useEffect(() => {
    fetch(`http://gestion.local/api/centros/${id}`)
      .then((response) => response.json())
      .then((data) => setCentro(data))
      .catch((error) => console.error("Error al cargar el centro:", error));
  }, [id]); // Se vuelve a ejecutar cuando cambia el ID del centro

  if (!centro) {
    return <p>Cargando...</p>; // Muestra un mensaje mientras se cargan los datos
  }

  return (
    <div style={{ padding: "20px" }}>
      <h1>{centro.nombre}</h1>
      <img src={centro.foto} alt={centro.nombre} style={{ width: "100%", borderRadius: "10px" }} />
      <p><strong>Dirección:</strong> {centro.direccion}</p>
      <p><strong>Teléfono:</strong> {centro.telefono}</p>
      <p><strong>Horario:</strong> {centro.horario}</p>

      <h3>Instalaciones</h3>
      <div style={{ display: "grid", gap: "20px" }}>
        {centro.instalaciones.map((instalacion) => (
          <div
            key={instalacion.id}
            style={{
              border: "1px solid #ddd",
              borderRadius: "10px",
              padding: "15px",
              boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
            }}
          >
            <h4>{instalacion.nombre}</h4>
            <p><strong>Descripción:</strong> {instalacion.descripcion}</p>
            <p><strong>Capacidad máxima:</strong> {instalacion.capacidad_maxima}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CentroUnico;
