import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function MiCuenta() {
  const [usuario, setUsuario] = useState(null);
  const [nombre, setNombre] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mensaje, setMensaje] = useState("");
  const navigate = useNavigate();

  // Función para obtener los datos del usuario
  const obtenerUsuario = async () => {
    const jwt = localStorage.getItem("jwt");
    if (!jwt) {
      navigate("/login"); // Si no está autenticado, redirige a login
      return;
    }

    try {
      const response = await fetch("http://gestion.local/user", {
        method: "GET",
        headers: {
          "Authorization": `Bearer ${jwt}`,
        },
      });
      if (response.ok) {
        const data = await response.json();
        setUsuario(data);
        setNombre(data.nombre);
        setEmail(data.email);
      } else {
        setMensaje("No se pudo obtener el usuario.");
      }
    } catch (error) {
      setMensaje("Error al obtener el usuario.");
      console.error(error);
    }
  };

  // Función para actualizar los datos del usuario
  const actualizarUsuario = async () => {
    const jwt = localStorage.getItem("jwt");
    if (!jwt) {
      navigate("/login"); // Si no está autenticado, redirige a login
      return;
    }

    const datosActualizados = {
      nombre,
      email,
      password,
    };

    try {
      const response = await fetch("http://gestion.local/user", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${jwt}`,
        },
        body: JSON.stringify(datosActualizados),
      });

      if (response.ok) {
        const data = await response.json();
        setMensaje("Datos actualizados correctamente.");
        obtenerUsuario(); // Actualizar los datos del usuario
      } else {
        setMensaje("Error al actualizar los datos.");
      }
    } catch (error) {
      setMensaje("Error al actualizar los datos.");
      console.error(error);
    }
  };

  // Función para eliminar el usuario
  const eliminarUsuario = async () => {
    const confirmacion = window.confirm("¿Quieres borrar tu usuario?");
    if (!confirmacion) return;

    const jwt = localStorage.getItem("jwt");
    if (!jwt) {
      navigate("/login"); // Si no está autenticado, redirige a login
      return;
    }

    try {
      const response = await fetch("http://gestion.local/user", {
        method: "DELETE",
        headers: {
          "Authorization": `Bearer ${jwt}`,
        },
      });

      if (response.ok) {
        localStorage.removeItem("jwt");
        localStorage.removeItem("expira");
        navigate("/login");
      } else {
        setMensaje("Error al eliminar el usuario.");
      }
    } catch (error) {
      setMensaje("Error al eliminar el usuario.");
      console.error(error);
    }
  };

  // Función para refrescar el token
  const refrescarToken = async () => {
    const jwt = localStorage.getItem("jwt");
    if (!jwt) {
      navigate("/login"); // Si no está autenticado, redirige a login
      return;
    }

    try {
      const response = await fetch("http://gestion.local/api/token/refresh", {
        method: "POST",
        headers: {
          "Authorization": `Bearer ${jwt}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem("jwt", data.jwt);
        localStorage.setItem("expira", data.expira);
        setMensaje("Token refrescado correctamente.");
      } else {
        setMensaje("Error al refrescar el token.");
      }
    } catch (error) {
      setMensaje("Error al refrescar el token.");
      console.error(error);
    }
  };

  useEffect(() => {
    obtenerUsuario(); // Obtener los datos del usuario cuando se monta el componente
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h1>Mi Cuenta</h1>
      {usuario ? (
        <div>
          <p><strong>Nombre:</strong> {usuario.nombre}</p>
          <p><strong>Email:</strong> {usuario.email}</p>

          <div>
            <h3>Actualizar Datos</h3>
            <div>
              <label>Nombre:</label>
              <input
                type="text"
                value={nombre}
                onChange={(e) => setNombre(e.target.value)}
              />
            </div>
            <div>
              <label>Email:</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div>
              <label>Contraseña:</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <button onClick={actualizarUsuario}>Actualizar</button>
          </div>

          <div>
            <button onClick={eliminarUsuario} style={{ backgroundColor: "red", color: "white" }}>
              Eliminar Cuenta
            </button>
          </div>

          <div>
            <button onClick={refrescarToken}>Refrescar Token</button>
          </div>
        </div>
      ) : (
        <p>Cargando datos de usuario...</p>
      )}

      {mensaje && <p>{mensaje}</p>}
    </div>
  );
}

export default MiCuenta;
