// src/App.js
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { useState, useEffect } from "react";
import './App.css';
import Centros from './Centros';
import CentroUnico from './CentroUnico';
import Login from './Login';
import Navbar from './Navbar';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Verificamos si hay un JWT en el localStorage para saber si el usuario está autenticado
  useEffect(() => {
    const jwt = localStorage.getItem("jwt");
    if (jwt) {
      setIsAuthenticated(true);
    }
  }, []);

  const logout = () => {
    localStorage.removeItem("jwt");
    localStorage.removeItem("expira");
    setIsAuthenticated(false); // Cambiar el estado para reflejar que el usuario ha cerrado sesión
  };

  return (
    <Router>
      <div className="App">
        <Navbar isAuthenticated={isAuthenticated} onLogout={logout} />
        <header>
          <h1>{titulo}</h1>
        </header>

        <Routes>
          <Route path="/" element={<Centros />} />
          <Route path="/centro/:id" element={<CentroUnico setTitulo={setTitulo} />} />
          {!isAuthenticated && <Route path="/login" element={<Login />} />}
          {/* Redirige a la página de login si el usuario no está autenticado */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
