import React, { useState, useEffect } from "react";
import "./Reservas.css"; // Asegúrate de agregar los estilos si los necesitas

const Reservas = () => {
  const [reservas, setReservas] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Obtener las reservas desde la API
    const fetchReservas = async () => {
      try {
        const token = localStorage.getItem("jwt");

        if (!token) {
          throw new Error("No se ha encontrado el token de autenticación.");
        }

        const response = await fetch("http://gestion.local/api/reservas", {
          method: "GET",
          headers: {
            "Authorization": `Bearer ${token}`, // Incluir token en la solicitud
            "Content-Type": "application/json",
          },
        });

        if (!response.ok) {
          throw new Error(`Error: ${response.status} ${response.statusText}`);
        }

        const data = await response.json();
        console.log("Datos de las reservas:", data); // Muestra las reservas en consola

        setReservas(data.reservas);
      } catch (error) {
        console.error("Error al cargar las reservas:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchReservas();
  }, []);

  return (
    <div className="reservas-container">
      <h1 className="reservas-title">Mis Reservas</h1>

      {loading ? (
        <p>Cargando reservas...</p>
      ) : reservas.length > 0 ? (
        <div className="reservas-list">
          {reservas.map((reserva) => (
            <div key={reserva.id} className="reserva-card">
              <h3 className="reserva-title">Reserva {reserva.id}</h3>
              <p><strong>Solicitante:</strong> {reserva.nombre_solicitante}</p>
              <p><strong>Teléfono:</strong> {reserva.telefono}</p>
              <p><strong>Correo:</strong> {reserva.correo}</p>
              <p><strong>Instalación:</strong> {reserva.instalacion_id}</p>
              <p><strong>Fecha y hora inicio:</strong> {new Date(reserva.fecha_hora_inicio).toLocaleString()}</p>
              <p><strong>Fecha y hora final:</strong> {new Date(reserva.fecha_hora_final).toLocaleString()}</p>
              <p><strong>Estado:</strong> {reserva.estado}</p>
            </div>
          ))}
        </div>
      ) : (
        <p>No se han encontrado reservas.</p>
      )}
    </div>
  );
};

export default Reservas;
