// src/instalaciones.js
import React, { useState, useEffect } from 'react';

const Instalaciones = () => {
  const [instalaciones, setInstalaciones] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredInstalaciones, setFilteredInstalaciones] = useState([]);

  useEffect(() => {
    // Función para obtener las instalaciones de la API
    const fetchInstalaciones = async () => {
      try {
        const response = await fetch('http://gestion.local/api/instalaciones');
        const data = await response.json();
        setInstalaciones(data);
        setFilteredInstalaciones(data); // Al principio mostramos todas las instalaciones
      } catch (error) {
        console.error('Error al cargar las instalaciones:', error);
      }
    };

    fetchInstalaciones();
  }, []);

  // Función que filtra las instalaciones en función del término de búsqueda
  const handleSearch = (event) => {
    const search = event.target.value;
    setSearchTerm(search);

    // Filtra las instalaciones basándose en el nombre
    const filtered = instalaciones.filter(instalacion =>
      instalacion.nombre.toLowerCase().includes(search.toLowerCase())
    );
    setFilteredInstalaciones(filtered);
  };

  return (
    <div className="instalaciones">
      <h1>Instalaciones</h1>

      <input
        type="text"
        placeholder="Buscar por nombre..."
        value={searchTerm}
        onChange={handleSearch}
      />

      <div>
        {filteredInstalaciones.length > 0 ? (
          <ul>
            {filteredInstalaciones.map(instalacion => (
              <li key={instalacion.id}>
                <h3>{instalacion.nombre}</h3>
                <p>{instalacion.descripcion}</p>
                <p>Capacidad máxima: {instalacion.capacidad_maxima}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No se encontraron instalaciones.</p>
        )}
      </div>
    </div>
  );
};

export default Instalaciones;
